  //   return this.http.patch(
  //     `Pedidos/${id}/status`,
  //     JSON.stringify(status),
  //     { headers }
  //   );
  // }

  atualizarStatus(id: number, status: string) {
  // O corpo é uma string JSON, não objeto
  return this.patch(`Pedidos/${id}/status`, JSON.stringify(status));
}


