import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Pedido } from 'src/app/core/models/pedido.model';

@Component({
  selector: 'app-pedido-list',
  templateUrl: './pedido-list.component.html',
  styleUrls: ['./pedido-list.component.scss']
})
export class PedidoListComponent  {
  @Input() pedidos: any[] = [];
  statusFiltro: string = '';

    constructor(private router: Router) {}
  filtrarPedidos() {

  }

  calcularTotal(pedido: any): number {
    return pedido.itens.reduce((soma: number, item: any) => soma + item.quantidade * item.precoUnitario, 0);
  }

  editarPedido(pedido: Pedido) {
    console.log('Editar pedido:', pedido);
    // Aqui você pode redirecionar ou abrir um modal


    this.router.navigate([`/pedidos/${pedido.id}/status`]);
  }

}
<label>Status:</label>
<select [(ngModel)]="statusFiltro" (change)="filtrarPedidos()">
  <option value="">Todos</option>
  <option value="Pendente">Pendente</option>
  <option value="Processado">Processado</option>
</select>
