import { Component, Output, EventEmitter } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'pedido-form',
  templateUrl: './pedido-form.component.html',
  styleUrls: ['./pedido-form.component.scss']
})
export class PedidoFormComponent {

  constructor(private router: Router) {}

  @Output() pedidoCriado = new EventEmitter<any>();
  status: string = 'Pendente';
  cliente: string = '';
  itens: any[] = [{ nomeProduto: '', quantidade: 1, precoUnitario: 0 }];

  adicionarItem() {
    this.itens.push({ nomeProduto: '', quantidade: 1, precoUnitario: 0 });
  }

  removerItem(index: number) {
    this.itens.splice(index, 1);
  }

  criarPedido() {
    const pedido = {
      cliente: this.cliente,
       status: this.status,
      itens: this.itens
    };
    this.pedidoCriado.emit(pedido);
  }

   irParaHome() {
    this.router.navigate(['']); // redireciona para rota raiz
  }
}
<form (ngSubmit)="criarPedido()" #pedidoForm="ngForm">
  <label>Cliente:</label>
  <input type="text" name="cliente" [(ngModel)]="cliente" required />

  <div *ngFor="let item of itens; let i = index">
    <label>Produto:</label>
    <input type="text" [(ngModel)]="item.nomeProduto" name="produto{{i}}" required />

    <label>Quantidade:</label>
    <input type="number" [(ngModel)]="item.quantidade" name="quantidade{{i}}" required />

    <label>Preço Unitário:</label>
    <input type="number" [(ngModel)]="item.precoUnitario" name="preco{{i}}" required />

    <button type="button" (click)="adicionarItem()">Adicionar Item</button>
    <button type="button" (click)="removerItem(i)">Remover</button>
  </div>

  <<form (ngSubmit)="criarPedido()" #pedidoForm="ngForm">
  <label>Cliente:</label>
  <input type="text" name="cliente" [(ngModel)]="cliente" required />

  <div *ngFor="let item of itens; let i = index">
    <label>Produto:</label>
    <input type="text" [(ngModel)]="item.nomeProduto" name="produto{{i}}" required />

    <label>Quantidade:</label>
    <input type="number" [(ngModel)]="item.quantidade" name="quantidade{{i}}" required />

    <label>Preço Unitário:</label>
    <input type="number" [(ngModel)]="item.precoUnitario" name="preco{{i}}" required />

    <button type="button" (click)="adicionarItem()">Adicionar Item</button>
    <button type="button" (click)="removerItem(i)">Remover</button>
  </div>

  <button type="submit">Criar Pedido</button>
  <button class="btn btn-primary" (click)="irParaHome()">Voltar</button>


</form>

</form>
