import { ActivatedRoute, Route, Router } from '@angular/router';
import { Component, OnInit } from '@angular/core';
import { PedidoService } from 'src/app/core/services/pedido.service';

@Component({
  selector: 'app-atualizar-status',
  templateUrl: './atualizar-status.component.html',
  styleUrls: ['./atualizar-status.component.scss']
})
export class AtualizarStatusComponent implements OnInit {
  idPedido!: number;
  novoStatus: string = 'Pendente';

  pedido: any = {};

  constructor(
    private route: ActivatedRoute,
    private pedidoService: PedidoService,
    private router: Router
  ) {}

  ngOnInit(): void {

    this.idPedido = Number(this.route.snapshot.paramMap.get('id'));
  }


  atualizarStatus(): void {
    console.log('teste:', this.novoStatus);
    this.pedidoService.atualizarStatus(this.idPedido, this.novoStatus)
      .subscribe({
        next: () => {
          alert('Status atualizado com sucesso!');
          this.router.navigate(['']);  // navega só depois do sucesso
        },
        error: err => console.error('Erro ao atualizar:', err)
      });
  }
}
<div class="status-container">

   <button class="btn-voltar" (click)="router.navigate([''])">← Voltar</button>
  <h2 class="titulo-status">Atualizar Status</h2>

  <label for="idPedido">ID do Pedido:</label>
  <input type="number" id="idPedido" [(ngModel)]="idPedido" class="input-status" />

  <app-status-selector [status]="novoStatus" (statusSelecionado)="novoStatus = $event"></app-status-selector>

  <button (click)="atualizarStatus()" class="btn-atualizar">Atualizar</button>
</div>
