import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-listar-pedidos',
  templateUrl: './listar-pedidos.component.html'
})
export class ListarPedidosComponent {
  pedidos: any[] = []; // sua lista de pedidos
  statusFiltro: string = '';

  constructor(private router: Router) {}

  filtrarPedidos() {
    // sua lógica aqui
  }

  calcularTotal(pedido: any): number {
    return pedido.itens.reduce((total: number, item: any) =>
      total + item.quantidade * item.precoUnitario, 0);
  }

  editarPedido(pedido: any) {
    this.router.navigate([`/pedidos/${pedido.id}/status`]);
  }
}
