import { Component } from '@angular/core';
import { PedidoService } from 'src/app/core/services/pedido.service';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-atualizar-status',
  templateUrl: './atualizar-status.component.html',
  styleUrls: ['./atualizar-status.component.scss']
})
export class AtualizarStatusComponent {

  idPedido: number = 0;
  novoStatus: string = 'Pendente';

  constructor(private pedidoService: PedidoService) {}

  atualizarStatus() {
    this.pedidoService.atualizarStatus(this.idPedido, this.novoStatus).subscribe();
  }
}
<h2>Atualizar Status</h2>
<label>ID do Pedido:</label>
<input type="number" [(ngModel)]="idPedido" />
<app-status-selector [status]="novoStatus" (statusSelecionado)="novoStatus = $event"></app-status-selector>
<button (click)="atualizarStatus()">Atualizar</button>
