import { Component } from '@angular/core';
import { PedidoService } from 'src/app/core/services/pedido.service';
import { Pedido } from 'src/app/core/models/pedido.model';

@Component({
  selector: 'app-criar-pedido',
  templateUrl: './criar-pedido.component.html',
  styleUrls: ['./criar-pedido.component.scss']
})
export class CriarPedidoComponent {
    cliente: string = '';
    status: string = 'Pendente'; // valor padrão
    itens: any[] = [
    { nomeProduto: '', quantidade: 1, precoUnitario: 0 }
  ];

    constructor(private pedidoService: PedidoService) {}

    adicionarItem() {
    this.itens.push({ nomeProduto: '', quantidade: 1, precoUnitario: 0 });
  }

  removerItem(index: number) {
    this.itens.splice(index, 1);
  }

  criarPedido() {
  const pedido: Pedido = {
    cliente: this.cliente,
    status: this.status,  // <-- adicionar status aqui
    itens: this.itens
  };

  this.pedidoService.criarPedido(pedido).subscribe({
    next: () => alert('Pedido criado com sucesso!'),
    error: (err) => console.error('Erro ao criar pedido:', err)
  });

    this.pedidoService.criarPedido(pedido).subscribe({
    next: () => alert('Pedido criado com sucesso!'),
    error: (err) => console.error('Erro ao criar pedido:', err)
  });
}


